// src/pages/driver/ProfilePage.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import DriverNavBar from "../../components/DriverNavBar";
import Header from "../components/Header";

export default function ProfilePage() {
  const navigate = useNavigate();
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Auth guard
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.role !== "delivery") {
      navigate("/");
    }
  }, [navigate]);

  const toggleSidebar = () => setSidebarOpen((o) => !o);

  // In a real app you'd fetch this; here are dummy values
  const user = JSON.parse(localStorage.getItem("user")) || {};
  const profileData = [
    {
      label: "Name",
      value: `${user.firstName || "Jane"} ${user.lastName || "Doe"}`,
    },
    { label: "Email", value: user.email || "jane.doe@example.com" },
    { label: "Username", value: user.username || "jane_doe" },
    { label: "Phone", value: user.phone || "071-123-4567" },
    { label: "Vehicle", value: user.vehicle || "Toyota Prius" },
    { label: "License Plate", value: user.license || "WP-AB-1234" },
    { label: "Home Base", value: user.location || "Colombo Fort" },
  ];

  return (
    <div
      className="flex h-screen overflow-hidden font-sans"
      style={{ fontFamily: "'Poppins', sans-serif" }}
    >
      {/* Sidebar */}
      <DriverNavBar
        className={`${
          sidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300`}
      />

      {/* Main */}
      <div className="flex-1 flex flex-col bg-gray-50 transition-all">
        <Header onToggleSidebar={toggleSidebar} />

        <main className="p-6 overflow-y-auto">
          <h2 className="text-2xl font-semibold mb-6">My Profile</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {profileData.map((field) => (
              <div
                key={field.label}
                className="bg-white bg-opacity-70 backdrop-blur-md rounded-xl shadow-lg p-5"
              >
                <span className="text-gray-600 text-sm">{field.label}</span>
                <p className="mt-2 text-lg font-medium text-gray-800">
                  {field.value}
                </p>
              </div>
            ))}
          </div>

          <div className="mt-8">
            <button
              onClick={() => alert("Edit profile flow here")}
              className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition"
            >
              Edit Profile
            </button>
          </div>
        </main>
      </div>
    </div>
  );
}
