import React, { useState, useEffect } from "react";
import NavBar from "../../components/NavBar";
import Header from "../../components/Header";
import Modal from "react-modal";

// Set the app element for react-modal
Modal.setAppElement("#root");

function ManageMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [restaurants, setRestaurants] = useState([
    // Demo restaurant data
    { _id: "1", name: "Delicious Diner" },
    { _id: "2", name: "Spicy Kitchen" },
    { _id: "3", name: "Fresh Fusion" },
  ]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState("");
  const [menuCategories, setMenuCategories] = useState([
    { id: "1", name: "Salad", image: "menu_1.png" },
    { id: "2", name: "Rolls", image: "menu_2.png" },
    { id: "3", name: "Deserts", image: "menu_3.png" },
    { id: "4", name: "Sandwich", image: "menu_4.png" },
    { id: "5", name: "Cake", image: "menu_5.png" },
    { id: "6", name: "Pure Veg", image: "menu_6.png" },
    { id: "7", name: "Pasta", image: "menu_7.png" },
    { id: "8", name: "Noodles", image: "menu_8.png" },
  ]);
  const [isAddCategoryModalOpen, setIsAddCategoryModalOpen] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [newCategoryImage, setNewCategoryImage] = useState(null);
  const [newCategoryImagePreviewUrl, setNewCategoryImagePreviewUrl] =
    useState("");
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [isAddMenuItemModalOpen, setIsAddMenuItemModalOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleRestaurantChange = (e) => {
    setSelectedRestaurantId(e.target.value);
    setSelectedCategory(null); // Reset selected category when restaurant changes
  };

  const openAddCategoryModal = () => {
    setIsAddCategoryModalOpen(true);
  };

  const closeAddCategoryModal = () => {
    setIsAddCategoryModalOpen(false);
    resetNewCategoryFields();
  };

  const handleNewCategoryInputChange = (e, setter) => {
    setter(e.target.value);
  };

  const handleNewCategoryImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewCategoryImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewCategoryImagePreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setNewCategoryImage(null);
      setNewCategoryImagePreviewUrl("");
    }
  };

  const resetNewCategoryFields = () => {
    setNewCategoryName("");
    setNewCategoryImage(null);
    setNewCategoryImagePreviewUrl("");
  };

  const handleSaveNewCategory = () => {
    if (newCategoryName && newCategoryImage) {
      const newCategory = {
        id: Date.now().toString(), // Temporary ID
        name: newCategoryName,
        image: "new_category_" + Date.now() + ".png", // Placeholder image name
      };
      setMenuCategories([...menuCategories, newCategory]);
      closeAddCategoryModal();
      // In a real application, you would upload the image and update the category data on the backend.
      alert(
        `Category "${newCategoryName}" added (image upload not implemented in this demo).`
      );
    } else {
      alert("Please enter a category name and select an image.");
    }
  };

  const handleCategoryClick = (category) => {
    if (selectedRestaurantId) {
      setSelectedCategory(category);
      setIsAddMenuItemModalOpen(true);
      console.log(
        `Selected Restaurant ID: ${selectedRestaurantId}, Category: ${category.name}`
      );
      // Here you would typically open a modal or navigate to a section
      // to add menu items for the selected restaurant and this category.
      // For now, we'll just open the add menu item modal.
    } else {
      alert("Please select a restaurant first.");
    }
  };

  const closeAddMenuItemModal = () => {
    setIsAddMenuItemModalOpen(false);
    setSelectedCategory(null);
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar (Navbar) */}
      <NavBar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <Header onToggleSidebar={toggleSidebar} />

        {/* Page Content */}
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h1 className="text-2xl font-semibold mb-4">Manage Menu Items</h1>

            <div className="mb-4">
              <label
                htmlFor="restaurant"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Select Restaurant:
              </label>
              <select
                id="restaurant"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={selectedRestaurantId}
                onChange={handleRestaurantChange}
              >
                <option value="">-- Select a Restaurant --</option>
                {restaurants.map((restaurant) => (
                  <option key={restaurant._id} value={restaurant._id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>

            {selectedRestaurantId && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-700">
                    Select Category
                  </h2>
                  <button
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    onClick={openAddCategoryModal}
                  >
                    Add New Category
                  </button>
                </div>

                <div className="flex flex-row space-x-4 mt-2 overflow-x-auto">
                  {menuCategories.map((category) => (
                    <div
                      key={category.id}
                      className={`relative rounded-full overflow-hidden shadow-md cursor-pointer hover:shadow-lg transition-shadow duration-300 w-32 h-32 flex-shrink-0 ${
                        selectedCategory?.id === category.id
                          ? "ring-2 ring-blue-500"
                          : ""
                      }`}
                      onClick={() => handleCategoryClick(category)}
                    >
                      <img
                        src={`/images/${category.image}`} // Assuming your images are in the public/images folder
                        alt={category.name}
                        className="w-full h-full object-cover"
                        style={{ aspectRatio: "1 / 1" }}
                      />
                      <div className="absolute bottom-0 left-0 right-0 bg-gray-900 bg-opacity-60 text-white py-2 text-center">
                        <span className="text-sm font-semibold">
                          {category.name}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {!selectedRestaurantId && (
              <p className="mt-4 text-gray-500">
                Please select a restaurant to manage its menu items.
              </p>
            )}

            {/* Modal for Adding New Category */}
            <Modal
              isOpen={isAddCategoryModalOpen}
              onRequestClose={closeAddCategoryModal}
              style={{
                overlay: {
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  zIndex: 1000,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  overflowY: "auto",
                },
                content: {
                  position: "relative",
                  background: "#fff",
                  borderRadius: "8px",
                  padding: "24px",
                  maxWidth: "400px",
                  width: "90%",
                  maxHeight: "80vh",
                  overflowY: "auto",
                },
              }}
              contentLabel="Add New Category"
            >
              <h2 className="text-xl font-semibold mb-4">Add New Category</h2>
              <form className="space-y-4">
                <div>
                  <label
                    htmlFor="newCategoryName"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Category Name:
                  </label>
                  <input
                    type="text"
                    id="newCategoryName"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newCategoryName}
                    onChange={(e) =>
                      handleNewCategoryInputChange(e, setNewCategoryName)
                    }
                    required
                  />
                </div>
                <div>
                  <label
                    htmlFor="newCategoryImage"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Category Image:
                  </label>
                  <input
                    type="file"
                    id="newCategoryImage"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    onChange={handleNewCategoryImageChange}
                    accept="image/*"
                    required
                  />
                  {newCategoryImagePreviewUrl && (
                    <div className="mt-2">
                      <img
                        src={newCategoryImagePreviewUrl}
                        alt="Category Preview"
                        className="max-h-32"
                      />
                    </div>
                  )}
                </div>
                <div className="flex justify-end">
                  <button
                    type="button"
                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline"
                    onClick={closeAddCategoryModal}
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    onClick={handleSaveNewCategory}
                  >
                    Save Category
                  </button>
                </div>
              </form>
            </Modal>

            {/* Modal for Adding New Menu Item (appears after selecting a category) */}
            <Modal
              isOpen={isAddMenuItemModalOpen}
              onRequestClose={closeAddMenuItemModal}
              style={{
                overlay: {
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  zIndex: 1000,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  overflowY: "auto",
                },
                content: {
                  position: "relative",
                  background: "#fff",
                  borderRadius: "8px",
                  padding: "24px",
                  maxWidth: "600px",
                  width: "90%",
                  maxHeight: "80vh",
                  overflowY: "auto",
                },
              }}
              contentLabel="Add New Menu Item"
            >
              <h2 className="text-xl font-semibold mb-4">
                Add New Menu Item for {selectedCategory?.name}
              </h2>
              {/* You would put your form for adding a new menu item here */}
              <p>
                Form to add new menu item for the selected category will go
                here.
              </p>
              <div className="flex justify-end mt-4">
                <button
                  type="button"
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline"
                  onClick={closeAddMenuItemModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  // onClick={handleSaveNewMenuItem} // Implement this function
                >
                  Add Menu Item
                </button>
              </div>
            </Modal>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageMenu;
