import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import CustomerDashboard from "./pages/CustomerDashboard";
import DriverDashboard from "./pages/DriverDashboard";
import AdminDashboard from "./pages/AdminDashboard";
import NavBar from "./components/NavBar";
import ManageRestaurants from "./pages/ManageRestaurants";
import ManageMenu from "./pages/ManageMenu";
import ManageOrders from "./pages/ManageOrders";
import AddRestaurant from "./pages/AddRestaurant";

function App() {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user"))); // Get user data from localStorage
  const navigate = useNavigate();

  useEffect(() => {
    // If the user is not logged in, redirect to login page
    if (!user) {
      navigate("/login");
    } else {
      // Redirect to the respective dashboard based on user role
      if (user.role === "admin") {
        navigate("/admin/dashboard");
      } else if (user.role === "customer") {
        navigate("/customer-dashboard");
      } else if (user.role === "delivery") {
        navigate("/driver-dashboard");
      }
    }
  }, [user, navigate]);

  return (
    <div>
      {user && user.role === "admin" && <NavBar />} {/* Show NavBar only if user is admin */}
      <Routes>
        {/* Register and Login should only be shown if no user is logged in */}
        <Route path="/register" element={!user ? <Register /> : <Login />} />
        <Route path="/" element={!user ? <Login /> : <AdminDashboard />} />
        <Route path="/login" element={!user ? <Login /> : <AdminDashboard />} />

        <Route path="/customer-dashboard" element={<CustomerDashboard />} />
        <Route path="/driver-dashboard" element={<DriverDashboard />} />

        {user && user.role === "admin" && (
          <>
            <Route path="/admin/dashboard" element={<AdminDashboard />} />
            <Route path="/admin/restaurants" element={<ManageRestaurants />} />
            <Route path="/admin/menu" element={<ManageMenu />} />
            <Route path="/admin/orders" element={<ManageOrders />} />
            <Route path="/admin/restaurants/add" element={<AddRestaurant />} />
          </>
        )}
      </Routes>
    </div>
  );
}

export default App;
