import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import CustomerHeader from "../../components/CustomerHeader";
import Footer from "../../components/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";

// Dummy menus keyed by restaurant ID:
const MENUS = {
  1: [
    {
      id: "1-1",
      name: "Chicken Curry",
      price: 500,
      img: "/images/",
    },
    { id: "1-2", name: "Fish Fry", price: 450, img: "/images/fish-fry.jpg" },
    // …etc
  ],
  2: [
    { id: "2-1", name: "Veg Noodles", price: 350, img: "/images/noodles.jpg" },
    { id: "2-2", name: "Spring Rolls", price: 300, img: "/images/rolls.jpg" },
  ],
  // add more restaurant menus…
};

export default function RestaurantDetailPage() {
  const { id } = useParams();
  const items = MENUS[id] || [];
  const [selected, setSelected] = useState({}); // { itemId: qty }

  const toggle = (itemId) => {
    setSelected((s) => {
      const copy = { ...s };
      if (copy[itemId]) delete copy[itemId];
      else copy[itemId] = 1;
      return copy;
    });
  };

  const inc = (itemId) => {
    setSelected((s) => ({ ...s, [itemId]: s[itemId] + 1 }));
  };

  const dec = (itemId) => {
    setSelected((s) => {
      const newQty = s[itemId] - 1;
      if (newQty <= 0) {
        const copy = { ...s };
        delete copy[itemId];
        return copy;
      }
      return { ...s, [itemId]: newQty };
    });
  };

  const clearAll = () => setSelected({});

  return (
    <div className="flex flex-col min-h-screen">
      <CustomerHeader />

      <main className="flex-grow container mx-auto px-4 py-8">
        <Link
          to="/restaurants"
          className="text-orange-500 hover:underline mb-4 inline-block"
        >
          ← Back to Restaurants
        </Link>

        <h1 className="text-2xl font-semibold mb-6">Menu</h1>

        <div className="space-y-4">
          {items.map((item) => (
            <div
              key={item.id}
              className="flex items-center bg-white rounded-lg shadow p-4"
            >
              <input
                type="checkbox"
                checked={!!selected[item.id]}
                onChange={() => toggle(item.id)}
                className="h-5 w-5 text-orange-500 focus:ring-orange-400 border-gray-300 rounded"
              />

              <img
                src={item.img}
                alt={item.name}
                className="w-16 h-16 object-cover rounded ml-4"
              />

              <div className="ml-4 flex-1">
                <h2 className="font-medium">{item.name}</h2>
                <p className="text-gray-600">Rs. {item.price.toFixed(2)}</p>
              </div>

              {selected[item.id] && (
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => dec(item.id)}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300"
                  >
                    <FontAwesomeIcon icon={faMinus} />
                  </button>
                  <span>{selected[item.id]}</span>
                  <button
                    onClick={() => inc(item.id)}
                    className="p-1 bg-gray-200 rounded-full hover:bg-gray-300"
                  >
                    <FontAwesomeIcon icon={faPlus} />
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>

        {Object.keys(selected).length > 0 && (
          <div className="mt-6">
            <button
              onClick={clearAll}
              className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
            >
              Clear All
            </button>
          </div>
        )}
      </main>

      <Footer />
    </div>
  );
}
