import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/api"; // Import your API service
import Navbar from "../../components/Navbar";
import Header from "../../components/Header";

function ManageRestaurants() {
  const navigate = useNavigate();
  const [restaurants, setRestaurants] = useState([]);
  const user = JSON.parse(localStorage.getItem("user")); // Assuming user info is in localStorage
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/");
      return;
    }

    const fetchAdminRestaurants = async () => {
      try {
        const response = await api.get(`/admin/restaurants/${user._id}`); // API call to fetch admin's restaurants
        setRestaurants(response.data);
      } catch (error) {
        console.error("Error fetching admin's restaurants:", error);
      }
    };

    fetchAdminRestaurants();
  }, [navigate, user]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleAddNewRestaurant = () => {
    navigate("/admin/restaurants/add");
  };

  const handleUpdateRestaurant = (restaurantId) => {
    navigate(`/admin/restaurants/update/${restaurantId}`);
  };

  const handleDeleteRestaurant = async (restaurantId) => {
    if (window.confirm("Are you sure you want to delete this restaurant?")) {
      try {
        await api.delete(`/admin/restaurants/${restaurantId}`);
        // Update the state to remove the deleted restaurant
        setRestaurants(
          restaurants.filter((restaurant) => restaurant._id !== restaurantId)
        );
        alert("Restaurant deleted successfully!");
      } catch (error) {
        console.error("Error deleting restaurant:", error);
        alert("Failed to delete restaurant.");
      }
    }
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar (Navbar) */}
      <Navbar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <Header onToggleSidebar={toggleSidebar} />

        {/* Page Content */}
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h2 className="text-2xl font-semibold mb-4">My Restaurants</h2>

            <button
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mb-4"
              onClick={handleAddNewRestaurant}
            >
              Add New Restaurant
            </button>

            {restaurants.length > 0 ? (
              <div className="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Name
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Location
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Status
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Open Time
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Close Time
                      </th>
                      <th scope="col" className="relative px-6 py-3">
                        <span className="sr-only">Edit/Delete</span>
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {restaurants.map((restaurant) => (
                      <tr key={restaurant._id}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {restaurant.name}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.address}{" "}
                          {/* Assuming 'address' holds the location */}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.isOpen ? "Open" : "Closed"}{" "}
                          {/* Assuming 'isOpen' boolean */}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.openTime}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.closeTime}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <button
                            onClick={() =>
                              handleUpdateRestaurant(restaurant._id)
                            }
                            className="text-indigo-600 hover:text-indigo-900 mr-2"
                          >
                            Update
                          </button>
                          <button
                            onClick={() =>
                              handleDeleteRestaurant(restaurant._id)
                            }
                            className="text-red-600 hover:text-red-900"
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p>No restaurants added yet.</p>
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageRestaurants;
