import React, { useEffect, useState } from "react";
import api from "../../services/api"; // Import your API service

function ManageRestaurants() {
  const [restaurants, setRestaurants] = useState([]);

  // Fetch all restaurants when the component mounts
  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await api.get("/restaurant"); // API call to fetch restaurants
        setRestaurants(response.data); // Set the restaurants data
      } catch (error) {
        console.error("Error fetching restaurants:", error);
      }
    };

    fetchRestaurants();
  }, []);

  return (
    <div className="manage-restaurants-container">
      <h2>Manage Restaurants</h2>
      <button
        className="add-restaurant-button"
        onClick={() => (window.location.href = "/admin/restaurants/add")}
      >
        Add New Restaurant
      </button>

      <div className="restaurant-tiles">
        {restaurants.map((restaurant) => (
          <div key={restaurant._id} className="restaurant-tile">
            <h3>{restaurant.name}</h3>
            <p>{restaurant.description}</p>
            <p>{restaurant.address}</p>
            <img src={restaurant.image} alt={restaurant.name} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default ManageRestaurants;
