import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import NavBar from "../../components/NavBar";
import Header from "../../components/Header";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";

function ManageRestaurants() {
  const navigate = useNavigate();
  const [restaurants, setRestaurants] = useState([
    {
      _id: "1",
      name: "Delicious Diner",
      address: "Colombo",
      isOpen: true,
      openTime: "08:00",
      closeTime: "23:00",
    },
    {
      _id: "2",
      name: "Spicy Kitchen",
      address: "Kandy",
      isOpen: false,
      openTime: "11:00",
      closeTime: "21:00",
    },
    {
      _id: "3",
      name: "Fresh Fusion",
      address: "Galle",
      isOpen: true,
      openTime: "10:00",
      closeTime: "22:00",
    },
    {
      _id: "4",
      name: "Burger Bliss",
      address: "Negombo",
      isOpen: true,
      openTime: "09:00",
      closeTime: "20:00",
    },
  ]);
  const user = JSON.parse(localStorage.getItem("user"));
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isAddingRestaurant, setIsAddingRestaurant] = useState(false);

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/");
    }
  }, [navigate, user]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleAddNewRestaurantClick = () => {
    setIsAddingRestaurant(true);
  };

  const handleRestaurantAdded = (newRestaurant) => {
    setRestaurants([
      ...restaurants,
      { ...newRestaurant, _id: Date.now().toString() },
    ]);
    setIsAddingRestaurant(false);
  };

  const handleUpdateRestaurant = (restaurantId) => {
    // For demo, we can just log the ID
    console.log("Update restaurant with ID:", restaurantId);
  };

  const handleDeleteRestaurant = (restaurantId) => {
    if (window.confirm("Are you sure you want to delete this restaurant?")) {
      setRestaurants(
        restaurants.filter((restaurant) => restaurant._id !== restaurantId)
      );
      alert("Restaurant deleted!"); // For demo purposes
    }
  };

  const handleCancelAddRestaurant = () => {
    setIsAddingRestaurant(false);
  };

  return (
    <div className="flex h-screen overflow-hidden">
      <NavBar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header onToggleSidebar={toggleSidebar} />
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h2 className="text-2xl font-semibold mb-4">My Restaurants</h2>

            {!isAddingRestaurant ? (
              <>
                <button
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mb-4"
                  onClick={handleAddNewRestaurantClick}
                >
                  Add New Restaurant
                </button>

                {restaurants.length > 0 ? (
                  <div className="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Name
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Location
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Status
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Open/Close Time
                          </th>
                          <th scope="col" className="relative px-6 py-3">
                            <span className="sr-only">Edit/Delete</span>
                          </th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {restaurants.map((restaurant) => (
                          <tr key={restaurant._id}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {restaurant.name}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {restaurant.address}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {restaurant.isOpen ? "Open" : "Closed"}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {`${restaurant.openTime}-${restaurant.closeTime}`}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                              <button
                                onClick={() =>
                                  handleUpdateRestaurant(restaurant._id)
                                }
                                className="text-indigo-600 hover:text-indigo-900 mr-2"
                              >
                                <FontAwesomeIcon icon={faEdit} />
                              </button>
                              <button
                                onClick={() =>
                                  handleDeleteRestaurant(restaurant._id)
                                }
                                className="text-red-600 hover:text-red-900"
                              >
                                <FontAwesomeIcon icon={faTrash} />
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <p>No restaurants added yet.</p>
                )}
              </>
            ) : (
              <AddRestaurant
                onRestaurantAdded={handleRestaurantAdded}
                onCancel={handleCancelAddRestaurant}
              />
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

// Modified AddRestaurant component to work within ManageRestaurants
function AddRestaurant({ onRestaurantAdded, onCancel }) {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [address, setAddress] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState("");
  const [isOpen, setIsOpen] = useState(true);
  const [openTime, setOpenTime] = useState("");
  const [closeTime, setCloseTime] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setImageFile(null);
      setImagePreviewUrl("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newRestaurant = {
      name,
      address,
      isOpen,
      openTime,
      closeTime,
      image: imagePreviewUrl, // Using preview URL for demo
      description,
    };
    onRestaurantAdded(newRestaurant);
  };

  return (
    <div className="bg-gray-100 p-4 rounded shadow-md">
      <h2 className="text-xl font-semibold mb-4">Add New Restaurant</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label
            htmlFor="name"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            Restaurant Name:
          </label>
          <input
            type="text"
            id="name"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label
            htmlFor="address"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            Location:
          </label>
          <input
            type="text"
            id="address"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>
        <div>
          <label
            htmlFor="isOpen"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            Status:
          </label>
          <select
            id="isOpen"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={isOpen}
            onChange={(e) => setIsOpen(e.target.value === "true")}
          >
            <option value={true}>Open</option>
            <option value={false}>Closed</option>
          </select>
        </div>
        <div className="flex space-x-4">
          <div>
            <label
              htmlFor="openTime"
              className="block text-gray-700 text-sm font-bold mb-2"
            >
              Open Time:
            </label>
            <input
              type="time"
              id="openTime"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={openTime}
              onChange={(e) => setOpenTime(e.target.value)}
            />
          </div>
          <div>
            <label
              htmlFor="closeTime"
              className="block text-gray-700 text-sm font-bold mb-2"
            >
              Close Time:
            </label>
            <input
              type="time"
              id="closeTime"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={closeTime}
              onChange={(e) => setCloseTime(e.target.value)}
            />
          </div>
        </div>
        <div>
          <label
            htmlFor="image"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            Image:
          </label>
          <input
            type="file"
            id="image"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleImageChange}
            accept="image/*"
          />
          {imagePreviewUrl && (
            <div className="mt-2">
              <img
                src={imagePreviewUrl}
                alt="Image Preview"
                className="max-h-40"
              />
            </div>
          )}
        </div>
        <div>
          <label
            htmlFor="description"
            className="block text-gray-700 text-sm font-bold mb-2"
          >
            Description:
          </label>
          <textarea
            id="description"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="flex justify-end">
          <button
            type="button"
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline"
            onClick={onCancel}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Add Restaurant
          </button>
        </div>
      </form>
    </div>
  );
}

export default ManageRestaurants;
