import React, { useState, useEffect } from "react";
import NavBar from "../../components/NavBar";
import Header from "../../components/Header";
import Modal from "react-modal";

// Set the app element for react-modal (important for accessibility)
Modal.setAppElement("#root"); // Or the root element of your app

function ManageMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [restaurants, setRestaurants] = useState([
    // Demo restaurant data
    { _id: "1", name: "Delicious Diner" },
    { _id: "2", name: "Spicy Kitchen" },
    { _id: "3", name: "Fresh Fusion" },
  ]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newItemName, setNewItemName] = useState("");
  const [newItemDescription, setNewItemDescription] = useState("");
  const [newItemPhoto, setNewItemPhoto] = useState(null);
  const [newItemPrice, setNewItemPrice] = useState("");
  const [newItemIngredients, setNewItemIngredients] = useState("");
  const [newItemSpecialNote, setNewItemSpecialNote] = useState("");
  const [imagePreviewUrl, setImagePreviewUrl] = useState("");

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    resetModalFields();
  };

  const handleRestaurantChange = (e) => {
    setSelectedRestaurantId(e.target.value);
  };

  const handleAddItem = () => {
    if (selectedRestaurantId) {
      openModal();
    } else {
      alert("Please select a restaurant first.");
    }
  };

  const handleInputChange = (e, setter) => {
    setter(e.target.value);
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewItemPhoto(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setNewItemPhoto(null);
      setImagePreviewUrl("");
    }
  };

  const resetModalFields = () => {
    setNewItemName("");
    setNewItemDescription("");
    setNewItemPhoto(null);
    setNewItemPrice("");
    setNewItemIngredients("");
    setNewItemSpecialNote("");
    setImagePreviewUrl("");
  };

  const handleSaveItem = () => {
    // In a real application, you would send this new item data
    // to your backend associated with the selectedRestaurantId.
    console.log("New Menu Item Data:", {
      restaurantId: selectedRestaurantId,
      name: newItemName,
      description: newItemDescription,
      photo: newItemPhoto,
      price: newItemPrice,
      ingredients: newItemIngredients,
      specialNote: newItemSpecialNote,
    });
    alert(
      `Menu item "${newItemName}" added to restaurant "${
        restaurants.find((r) => r._id === selectedRestaurantId)?.name
      }" (demo)!`
    );
    closeModal();
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar (Navbar) */}
      <NavBar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <Header onToggleSidebar={toggleSidebar} />

        {/* Page Content */}
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h1 className="text-2xl font-semibold mb-4">Manage Menu Items</h1>

            <div className="mb-4">
              <label
                htmlFor="restaurant"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Select Restaurant:
              </label>
              <select
                id="restaurant"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={selectedRestaurantId}
                onChange={handleRestaurantChange}
              >
                <option value="">-- Select a Restaurant --</option>
                {restaurants.map((restaurant) => (
                  <option key={restaurant._id} value={restaurant._id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>

            <button
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={handleAddItem}
              disabled={!selectedRestaurantId}
            >
              Add New Menu Item
            </button>

            {/* Modal for adding menu items */}
            <Modal
              isOpen={isModalOpen}
              onRequestClose={closeModal}
              style={{
                overlay: {
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  zIndex: 1000,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  overflowY: "auto", // Enable scrolling for the overlay if needed
                },
                content: {
                  position: "relative",
                  background: "#fff",
                  borderRadius: "8px",
                  padding: "24px",
                  maxWidth: "600px",
                  width: "90%",
                  maxHeight: "80vh", // Limit the maximum height of the content
                  overflowY: "auto", // Enable scrolling within the content
                },
              }}
              contentLabel="Add New Menu Item"
            >
              <h2 className="text-xl font-semibold mb-4">Add New Menu Item</h2>
              <form className="space-y-4">
                <div>
                  <label
                    htmlFor="itemName"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Item Name:
                  </label>
                  <input
                    type="text"
                    id="itemName"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newItemName}
                    onChange={(e) => handleInputChange(e, setNewItemName)}
                    required
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemDescription"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Description:
                  </label>
                  <textarea
                    id="itemDescription"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newItemDescription}
                    onChange={(e) =>
                      handleInputChange(e, setNewItemDescription)
                    }
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemPhoto"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Photo:
                  </label>
                  <input
                    type="file"
                    id="itemPhoto"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    onChange={handlePhotoChange}
                    accept="image/*"
                  />
                  {imagePreviewUrl && (
                    <div className="mt-2">
                      <img
                        src={imagePreviewUrl}
                        alt="Item Preview"
                        className="max-h-40"
                      />
                    </div>
                  )}
                </div>
                <div>
                  <label
                    htmlFor="itemPrice"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Price:
                  </label>
                  <input
                    type="number"
                    id="itemPrice"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newItemPrice}
                    onChange={(e) => handleInputChange(e, setNewItemPrice)}
                    required
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemIngredients"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Ingredients:
                  </label>
                  <textarea
                    id="itemIngredients"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newItemIngredients}
                    onChange={(e) =>
                      handleInputChange(e, setNewItemIngredients)
                    }
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemSpecialNote"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Special Note (Optional):
                  </label>
                  <textarea
                    id="itemSpecialNote"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={newItemSpecialNote}
                    onChange={(e) =>
                      handleInputChange(e, setNewItemSpecialNote)
                    }
                  />
                </div>
                <div className="flex justify-end">
                  <button
                    type="button"
                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline"
                    onClick={closeModal}
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    onClick={handleSaveItem}
                  >
                    Save Item
                  </button>
                </div>
              </form>
            </Modal>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageMenu;
