// src/pages/customer-resturents/RestaurantDetailPage.js
import React, { useState } from "react";
import { useParams, Link, useLocation } from "react-router-dom";
import CustomerHeader from "../../components/CustomerHeader";
import Footer from "../../components/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlus,
  faMinus,
  faShoppingCart,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";

// Dummy menus keyed by restaurant ID:
const MENUS = {
  1: [
    /* ... */
  ],
  2: [
    /* ... */
  ],
};

export default function RestaurantDetailPage() {
  const { id } = useParams();
  const location = useLocation();

  // Try to read `banner` and `name` from the Link state
  const { banner, name: restName } = location.state || {};

  // Fallbacks if someone landed here directly (no state)
  const heroImg = banner || "/images/restaurant-hero.jpg";
  const restaurantName = restName || "Restaurant";

  const items = MENUS[id] || [];
  const [selected, setSelected] = useState({});

  // … toggle/inc/dec/clearAll same as before …

  const totalItems = Object.values(selected).reduce((a, b) => a + b, 0);

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      <CustomerHeader />

      <main className="flex-grow">
        {/* Hero Banner uses passed-in banner image */}
        <div
          className="relative h-64 bg-center bg-cover"
          style={{ backgroundImage: `url(${heroImg})` }}
        >
          <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center">
            <h1 className="text-white text-4xl md:text-5xl font-extrabold">
              {restaurantName}
            </h1>
          </div>
        </div>

        <div className="container mx-auto px-4 py-8">
          <Link
            to="/restaurants"
            className="inline-block text-orange-500 hover:underline mb-6"
          >
            ← Back to Restaurants
          </Link>

          {/* … your menu grid & “Add to Cart” logic … */}
        </div>
      </main>

      {/* … sticky footer bar if totalItems > 0 … */}

      <Footer />
    </div>
  );
}
