import React, { useState, useEffect } from "react";
import NavBar from "../../components/NavBar";
import Header from "../../components/Header";
import Modal from "react-modal";

// Set the app element for react-modal
Modal.setAppElement("#root");

function ManageMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [restaurants, setRestaurants] = useState([
    { _id: "1", name: "Delicious Diner" },
    { _id: "2", name: "Spicy Kitchen" },
    { _id: "3", name: "Fresh Fusion" },
  ]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState("");
  const [menuCategories, setMenuCategories] = useState([
    { id: "salad", name: "Salad", image: "menu_1.png" },
    { id: "rolls", name: "Rolls", image: "menu_2.png" },
    { id: "deserts", name: "Deserts", image: "menu_3.png" },
    { id: "sandwich", name: "Sandwich", image: "menu_4.png" },
    { id: "cake", name: "Cake", image: "menu_5.png" },
    { id: "pure-veg", name: "Pure Veg", image: "menu_6.png" },
    { id: "pasta", name: "Pasta", image: "menu_7.png" },
    { id: "noodles", name: "Noodles", image: "menu_8.png" },
  ]);
  const [isAddCategoryModalOpen, setIsAddCategoryModalOpen] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [newCategoryImage, setNewCategoryImage] = useState(null);
  const [newCategoryImagePreviewUrl, setNewCategoryImagePreviewUrl] =
    useState("");
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [menuItemsInCategory, setMenuItemsInCategory] = useState([]);
  const [isAddMenuItemModalOpen, setIsAddMenuItemModalOpen] = useState(false);

  const [newItemName, setNewItemName] = useState("");
  const [newItemDescription, setNewItemDescription] = useState("");
  const [newItemPrice, setNewItemPrice] = useState("");

  const dummyMenuItems = [
    {
      id: "s1",
      restaurantId: "1",
      categoryId: "salad",
      name: "Greek Salad",
      description: "Fresh and healthy Greek salad.",
      price: 12,
      image: "greek-salad.jpg",
    },
    {
      id: "s2",
      restaurantId: "1",
      categoryId: "salad",
      name: "Italian Chopped Salad",
      description: "Delicious Italian chopped salad.",
      price: 15,
      image: "ItalianChoppedSalad.webp",
    },
    {
      id: "r1",
      restaurantId: "1",
      categoryId: "rolls",
      name: "Chicken Spring Rolls",
      description: "Savory chicken spring rolls.",
      price: 8,
      image: "Chicken-Spring-Rolls.jpg",
    },
    {
      id: "d1",
      restaurantId: "1",
      categoryId: "deserts",
      name: "Panna Cotta",
      description: "Classic Italian panna cotta.",
      price: 10,
      image: "Panna-Cotta-desert.jpg",
    },
  ];

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleRestaurantChange = (e) => {
    setSelectedRestaurantId(e.target.value);
    setSelectedCategory(null);
    setMenuItemsInCategory([]);
  };

  const openAddCategoryModal = () => {
    setIsAddCategoryModalOpen(true);
  };

  const closeAddCategoryModal = () => {
    setIsAddCategoryModalOpen(false);
    resetNewCategoryFields();
  };

  const handleNewCategoryInputChange = (e, setter) => {
    setter(e.target.value);
  };

  const handleNewCategoryImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewCategoryImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewCategoryImagePreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setNewCategoryImage(null);
      setNewCategoryImagePreviewUrl("");
    }
  };

  const resetNewCategoryFields = () => {
    setNewCategoryName("");
    setNewCategoryImage(null);
    setNewCategoryImagePreviewUrl("");
  };

  const handleSaveNewCategory = () => {
    if (newCategoryName && newCategoryImage) {
      const newCategory = {
        id: newCategoryName.toLowerCase().replace(/\s+/g, "-"),
        name: newCategoryName,
        image: "new_category_" + Date.now() + ".png",
      };
      setMenuCategories([...menuCategories, newCategory]);
      closeAddCategoryModal();
      alert(
        `Category "${newCategoryName}" added (image upload not implemented).`
      );
    } else {
      alert("Please enter a category name and select an image.");
    }
  };

  const handleCategoryClick = (category) => {
    if (selectedRestaurantId) {
      setSelectedCategory(category);
      const items = dummyMenuItems.filter(
        (item) =>
          item.restaurantId === selectedRestaurantId &&
          item.categoryId === category.id
      );
      setMenuItemsInCategory(items);
    } else {
      alert("Please select a restaurant first.");
    }
  };

  const openAddMenuItemModal = () => {
    setIsAddMenuItemModalOpen(true);
  };

  const closeAddMenuItemModal = () => {
    setIsAddMenuItemModalOpen(false);
    setNewItemName("");
    setNewItemDescription("");
    setNewItemPrice("");
  };

  const handleSaveNewMenuItem = () => {
    if (
      newItemName &&
      newItemDescription &&
      newItemPrice &&
      selectedCategory &&
      selectedRestaurantId
    ) {
      const newItem = {
        id: Date.now().toString(),
        restaurantId: selectedRestaurantId,
        categoryId: selectedCategory.id,
        name: newItemName,
        description: newItemDescription,
        price: parseFloat(newItemPrice),
        image: "default-image.jpg", // Default image for now
      };

      setMenuItemsInCategory([...menuItemsInCategory, newItem]);
      closeAddMenuItemModal();
    } else {
      alert("Please fill all the fields.");
    }
  };

  return (
    <div className="flex h-screen overflow-hidden">
      <NavBar
        className={`fixed top-0 left-0 h-full bg-gray-800 transition-all duration-300 ${
          isSidebarOpen ? "w-64" : "w-20"
        }`}
      />

      <div
        className={`flex-1 flex flex-col transition-all duration-300 ${
          isSidebarOpen ? "ml-64" : "ml-20"
        }`}
      >
        <Header onToggleSidebar={toggleSidebar} />

        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h1 className="text-2xl font-semibold mb-4">Manage Menu Items</h1>

            <div className="mb-4">
              <label
                htmlFor="restaurant"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Select Restaurant:
              </label>
              <select
                id="restaurant"
                className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight"
                value={selectedRestaurantId}
                onChange={handleRestaurantChange}
              >
                <option value="">-- Select a Restaurant --</option>
                {restaurants.map((restaurant) => (
                  <option key={restaurant._id} value={restaurant._id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>

            {selectedRestaurantId && !selectedCategory && (
              <>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-700">
                    Select Category
                  </h2>
                  <button
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                    onClick={openAddCategoryModal}
                  >
                    Add New Category
                  </button>
                </div>
                <div className="flex space-x-4 overflow-x-auto">
                  {menuCategories.map((category) => (
                    <div
                      key={category.id}
                      className="relative rounded-full overflow-hidden shadow-md cursor-pointer w-32 h-32"
                      onClick={() => handleCategoryClick(category)}
                    >
                      <img
                        src={`/images/${category.image}`}
                        alt={category.name}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute bottom-0 left-0 right-0 bg-gray-900 bg-opacity-60 text-white text-center py-1">
                        <span className="text-sm font-semibold">
                          {category.name}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </>
            )}

            {selectedRestaurantId && selectedCategory && (
              <>
                <h2 className="text-xl font-semibold mb-4">
                  Menu Items in {selectedCategory.name}
                </h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
                  {menuItemsInCategory.map((item) => (
                    <div
                      key={item.id}
                      className="bg-white rounded-lg shadow-md overflow-hidden"
                    >
                      <img
                        src={`/images/${item.image}`}
                        alt={item.name}
                        className="w-full h-32 object-cover"
                      />
                      <div className="p-4">
                        <h3 className="text-lg font-semibold">{item.name}</h3>
                        <p className="text-gray-600 text-sm">
                          {item.description}
                        </p>
                        <p className="text-indigo-500 font-bold">
                          ${item.price.toFixed(2)}
                        </p>
                      </div>
                    </div>
                  ))}
                  <div
                    className="bg-gray-100 rounded-lg shadow-md flex items-center justify-center cursor-pointer hover:bg-gray-200"
                    onClick={openAddMenuItemModal}
                  >
                    <div className="text-gray-500 text-center">
                      <svg
                        className="w-10 h-10 mx-auto"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth="2"
                          d="M12 6v12m6-6H6"
                        />
                      </svg>
                      <p className="text-sm">Add New Item</p>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>

          {/* Modal for Adding New Menu Item */}
          <Modal
            isOpen={isAddMenuItemModalOpen}
            onRequestClose={closeAddMenuItemModal}
            style={{
              overlay: {
                backgroundColor: "rgba(0, 0, 0, 0.6)",
                zIndex: 1000,
              },
              content: {
                background: "#fff",
                borderRadius: "8px",
                padding: "24px",
                maxWidth: "600px",
                margin: "auto",
              },
            }}
            contentLabel="Add New Menu Item"
          >
            <h2 className="text-xl font-semibold mb-4">Add New Menu Item</h2>
            <form className="space-y-4">
              <input
                type="text"
                placeholder="Item Name"
                className="w-full p-2 border rounded"
                value={newItemName}
                onChange={(e) => setNewItemName(e.target.value)}
              />
              <textarea
                placeholder="Item Description"
                className="w-full p-2 border rounded"
                value={newItemDescription}
                onChange={(e) => setNewItemDescription(e.target.value)}
              />
              <input
                type="number"
                placeholder="Item Price"
                className="w-full p-2 border rounded"
                value={newItemPrice}
                onChange={(e) => setNewItemPrice(e.target.value)}
              />
              <div className="flex justify-end">
                <button
                  type="button"
                  onClick={closeAddMenuItemModal}
                  className="px-4 py-2 mr-2 bg-gray-300 rounded hover:bg-gray-400"
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleSaveNewMenuItem}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700"
                >
                  Save
                </button>
              </div>
            </form>
          </Modal>
        </main>
      </div>
    </div>
  );
}

export default ManageMenu;
