import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar"; // Corrected import
import Header from "../components/Header";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  LineElement,
  PointElement,
  LineController,
  PieController,
  ArcElement,
} from "chart.js";
import { Bar, Line, Pie } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  LineElement,
  PointElement,
  LineController,
  PieController,
  ArcElement
);

function AdminDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/");
    }
  }, [user, navigate]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // Sample data for Customer Map (Bar Chart)
  const customerMapData = {
    labels: ["Area A", "Area B", "Area C", "Area D", "Area E", "Area F"],
    datasets: [
      {
        label: "Number of Customers",
        data: [150, 210, 80, 320, 110, 180],
        backgroundColor: [
          "#FF6B6B",
          "#4BC0C0",
          "#FFDA6B",
          "#B370CF",
          "#76D7EA",
          "#F7DC6F",
        ],
      },
    ],
  };

  const customerMapOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: "Customer Map",
      },
      legend: {
        display: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Number of Customers",
        },
      },
    },
  };

  // Sample data for Overview (Pie Chart)
  const overviewData = {
    labels: ["Total Order", "Customer Growth", "Total Revenue"],
    datasets: [
      {
        data: [81, 22, 12],
        backgroundColor: ["#F44336", "#4CAF50", "#2196F3"],
        hoverOffset: 4,
      },
    ],
  };

  const overviewOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: "bottom",
      },
      title: {
        display: true,
        text: "Overview",
      },
    },
  };

  // Sample data for Order Chart (Line Chart)
  const orderChartData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul"],
    datasets: [
      {
        label: "Orders",
        data: [50, 65, 80, 70, 95, 85, 100],
        borderColor: "#9C27B0",
        tension: 0.3,
        fill: false,
      },
    ],
  };

  const orderChartOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: "Order Chart",
      },
      legend: {
        display: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Number of Orders",
        },
      },
    },
  };

  // Sample data for Total Revenue Trend (Line Chart)
  const revenueChartData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul"],
    datasets: [
      {
        label: "Revenue ($)",
        data: [1200, 1500, 1800, 1600, 2000, 1900, 2200],
        borderColor: "#2E7D32",
        tension: 0.3,
        fill: false,
      },
    ],
  };

  const revenueChartOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: "Total Revenue Trend",
      },
      legend: {
        display: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Revenue ($)",
        },
      },
    },
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar (Navbar) */}
      <Navbar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <Header onToggleSidebar={toggleSidebar} />

        {/* Dashboard Content */}
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Top Widgets (remain in the first row) */}
            <div className="bg-white shadow rounded-md p-4">
              <h3 className="text-lg font-semibold text-gray-700">
                Total Orders
              </h3>
              <p className="text-2xl font-bold text-green-500">75</p>
            </div>
            <div className="bg-white shadow rounded-md p-4">
              <h3 className="text-lg font-semibold text-gray-700">
                Transactions
              </h3>
              <p className="text-2xl font-bold text-green-500">357</p>
            </div>
            <div className="bg-white shadow rounded-md p-4">
              <h3 className="text-lg font-semibold text-gray-700">
                New Customers
              </h3>
              <p className="text-2xl font-bold text-green-500">65</p>
            </div>
            <div className="bg-white shadow rounded-md p-4">
              <h3 className="text-lg font-semibold text-gray-700">
                Total Revenue
              </h3>
              <p className="text-2xl font-bold text-green-500">$128</p>
            </div>

            {/* Charts in the next rows, adjusting col-span for layout */}
            <div className="bg-white shadow rounded-md p-4 md:col-span-2">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                Overview
              </h3>
              <Pie data={overviewData} options={overviewOptions} />
            </div>

            <div className="bg-white shadow rounded-md p-4 md:col-span-2">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                Order Chart
              </h3>
              <Line data={orderChartData} options={orderChartOptions} />
            </div>

            <div className="bg-white shadow rounded-md p-4 md:col-span-2">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                Total Revenue Trend
              </h3>
              <Line data={revenueChartData} options={revenueChartOptions} />
            </div>

            <div className="bg-white shadow rounded-md p-4 md:col-span-2">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                Customer Map
              </h3>
              <Bar options={customerMapOptions} data={customerMapData} />
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default AdminDashboard;
