// src/pages/customer-resturents/PaymentPage.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import CustomerHeader from "../../components/CustomerHeader";
import Footer from "../../components/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronLeft } from "@fortawesome/free-solid-svg-icons";

export default function PaymentPage() {
  const navigate = useNavigate();
  const { state } = useLocation();
  const { orderItems = [], restName = "" } = state || {};

  const [address, setAddress] = useState("");

  const total = orderItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleConfirm = () => {
    // Here you’d call your API to create the order...
    alert(
      `Order placed!\nRestaurant: ${restName}\nTotal: Rs. ${total.toFixed(
        2
      )}\nAddress: ${address}`
    );
    navigate("/"); // back to home (or orders page)
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      <CustomerHeader />

      <main className="flex-grow container mx-auto px-4 py-8">
        <button
          onClick={() => navigate(-1)}
          className="text-orange-500 hover:underline mb-4 flex items-center"
        >
          <FontAwesomeIcon icon={faChevronLeft} className="mr-2" />
          Back
        </button>

        <h1 className="text-2xl font-semibold mb-6">Payment for {restName}</h1>

        <div className="bg-white rounded-xl shadow p-6 mb-6">
          <h2 className="font-semibold mb-4">Order Summary</h2>
          <ul className="divide-y divide-gray-200">
            {orderItems.map((item) => (
              <li
                key={item.id}
                className="py-3 flex justify-between items-center"
              >
                <div>
                  <p className="font-medium">
                    {item.name} × {item.quantity}
                  </p>
                </div>
                <div>
                  <p>Rs. {(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </li>
            ))}
          </ul>
          <div className="pt-4 flex justify-between font-semibold text-lg">
            <span>Total</span>
            <span>Rs. {total.toFixed(2)}</span>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow p-6 mb-6">
          <h2 className="font-semibold mb-4">Delivery Address</h2>
          <textarea
            rows={3}
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Enter your delivery address..."
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-400"
          />
        </div>

        <button
          onClick={handleConfirm}
          disabled={!address}
          className={`w-full py-3 rounded-lg text-white font-semibold transition ${
            address
              ? "bg-orange-500 hover:bg-orange-600"
              : "bg-gray-300 cursor-not-allowed"
          }`}
        >
          Confirm Payment
        </button>
      </main>

      <Footer />
    </div>
  );
}
