import React, { useState,useEffect  } from "react";
import { Routes, Route, useNavigate } from "react-router-dom"; // Import Routes, Route, and useNavigate

import Register from "./pages/Register";
import Login from "./pages/Login";
import CustomerDashboard from "./pages/CustomerDashboard";
import DriverDashboard from "./pages/DriverDashboard";
import AdminDashboard from "./pages/AdminDashboard";
import NavBar from "./components/NavBar";
import ManageRestaurants from "./pages/ManageRestaurants";
import ManageMenu from "./pages/ManageMenu";
import ManageOrders from "./pages/ManageOrders";

function App() {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user"))); // Get user data from localStorage
  const navigate = useNavigate(); // useNavigate hook to programmatically navigate

  // Redirect user to login page if not authenticated
  useEffect(() => {
    if (!user) {
      navigate("/login"); // Redirect to login if no user
    }
  }, [user, navigate]);

  return (
    <div>
      {user && user.role === "admin" && <NavBar />} {/* Show NavBar if admin */}
      <Routes>
        <Route path="/register" element={<Register />} />
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/customer-dashboard" element={<CustomerDashboard />} />
        <Route path="/driver-dashboard" element={<DriverDashboard />} />
        {user && user.role === "admin" && (
          <>
            <Route path="/admin/dashboard" element={<AdminDashboard />} />
            <Route path="/admin/restaurants" element={<ManageRestaurants />} />
            <Route path="/admin/menu" element={<ManageMenu />} />
            <Route path="/admin/orders" element={<ManageOrders />} />
          </>
        )}
      </Routes>
    </div>
  );
}

export default App;
