import React, { useState, useEffect } from "react";
import NavBar from "../../components/NavBar";
import Header from "../../components/Header";
import Modal from "react-modal";

// Set the app element for react-modal
Modal.setAppElement("#root");

function ManageMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [restaurants, setRestaurants] = useState([
    { _id: "1", name: "Delicious Diner" },
    { _id: "2", name: "Spicy Kitchen" },
    { _id: "3", name: "Fresh Fusion" },
  ]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState("");
  const [menuCategories, setMenuCategories] = useState([
    { id: "salad", name: "Salad", image: "menu_1.png" },
    { id: "rolls", name: "Rolls", image: "menu_2.png" },
    { id: "deserts", name: "Deserts", image: "menu_3.png" },
    { id: "sandwich", name: "Sandwich", image: "menu_4.png" },
    { id: "cake", name: "Cake", image: "menu_5.png" },
    { id: "pure-veg", name: "Pure Veg", image: "menu_6.png" },
    { id: "pasta", name: "Pasta", image: "menu_7.png" },
    { id: "noodles", name: "Noodles", image: "menu_8.png" },
  ]);
  const [isAddCategoryModalOpen, setIsAddCategoryModalOpen] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [newCategoryImage, setNewCategoryImage] = useState(null);
  const [newCategoryImagePreviewUrl, setNewCategoryImagePreviewUrl] =
    useState("");
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [menuItemsInCategory, setMenuItemsInCategory] = useState([]);
  const [isAddMenuItemModalOpen, setIsAddMenuItemModalOpen] = useState(false);

  // Dummy menu item data (replace with your actual data fetching)
  const dummyMenuItems = [
    {
      id: "s1",
      restaurantId: "1",
      categoryId: "salad",
      name: "Greek Salad",
      description: "Fresh and healthy Greek salad.",
      price: 12,
      image: "greek-salad.jpg",
    },
    {
      id: "s2",
      restaurantId: "1",
      categoryId: "salad",
      name: "Italian Chopped Salad",
      description: "Delicious Italian chopped salad.",
      price: 15,
      image: "ItalianChoppedSalad.webp",
    },
    {
      id: "r1",
      restaurantId: "1",
      categoryId: "rolls",
      name: "Chicken Spring Rolls",
      description: "Savory chicken spring rolls.",
      price: 8,
      image: "Chicken-Spring-Rolls.jpg",
    },
    {
      id: "d1",
      restaurantId: "1",
      categoryId: "deserts",
      name: "Panna Cotta",
      description: "Classic Italian panna cotta.",
      price: 10,
      image: "Panna-Cotta-desert.jpg",
    },
    // Add more dummy items for other categories and restaurants as needed
  ];

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleRestaurantChange = (e) => {
    setSelectedRestaurantId(e.target.value);
    setSelectedCategory(null);
    setMenuItemsInCategory([]);
  };

  const openAddCategoryModal = () => {
    setIsAddCategoryModalOpen(true);
  };

  const closeAddCategoryModal = () => {
    setIsAddCategoryModalOpen(false);
    resetNewCategoryFields();
  };

  const handleNewCategoryInputChange = (e, setter) => {
    setter(e.target.value);
  };

  const handleNewCategoryImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewCategoryImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewCategoryImagePreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setNewCategoryImage(null);
      setNewCategoryImagePreviewUrl("");
    }
  };

  const resetNewCategoryFields = () => {
    setNewCategoryName("");
    setNewCategoryImage(null);
    setNewCategoryImagePreviewUrl("");
  };

  const handleSaveNewCategory = () => {
    if (newCategoryName && newCategoryImage) {
      const newCategory = {
        id: newCategoryName.toLowerCase().replace(/\s+/g, "-"), // Basic ID generation
        name: newCategoryName,
        image: "new_category_" + Date.now() + ".png", // Placeholder
      };
      setMenuCategories([...menuCategories, newCategory]);
      closeAddCategoryModal();
      alert(
        `Category "${newCategoryName}" added (image upload not implemented).`
      );
    } else {
      alert("Please enter a category name and select an image.");
    }
  };

  const handleCategoryClick = (category) => {
    if (selectedRestaurantId) {
      setSelectedCategory(category);
      // Filter menu items for the selected restaurant and category
      const items = dummyMenuItems.filter(
        (item) =>
          item.restaurantId === selectedRestaurantId &&
          item.categoryId === category.id
      );
      setMenuItemsInCategory(items);
    } else {
      alert("Please select a restaurant first.");
    }
  };

  const openAddMenuItemModal = () => {
    setIsAddMenuItemModalOpen(true);
  };

  const closeAddMenuItemModal = () => {
    setIsAddMenuItemModalOpen(false);
  };

  return (
    <div className="flex h-screen overflow-hidden">
      <NavBar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header onToggleSidebar={toggleSidebar} />

        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h1 className="text-2xl font-semibold mb-4">Manage Menu Items</h1>

            <div className="mb-4">
              <label
                htmlFor="restaurant"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Select Restaurant:
              </label>
              <select
                id="restaurant"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={selectedRestaurantId}
                onChange={handleRestaurantChange}
              >
                <option value="">-- Select a Restaurant --</option>
                {restaurants.map((restaurant) => (
                  <option key={restaurant._id} value={restaurant._id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>

            {selectedRestaurantId && !selectedCategory && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-700">
                    Select Category
                  </h2>
                  <button
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    onClick={openAddCategoryModal}
                  >
                    Add New Category
                  </button>
                </div>
                <div className="flex flex-row space-x-4 mt-2 overflow-x-auto">
                  {menuCategories.map((category) => (
                    <div
                      key={category.id}
                      className="relative rounded-full overflow-hidden shadow-md cursor-pointer hover:shadow-lg transition-shadow duration-300 w-32 h-32 flex-shrink-0"
                      onClick={() => handleCategoryClick(category)}
                    >
                      <img
                        src={`/images/${category.image}`}
                        alt={category.name}
                        className="w-full h-full object-cover"
                        style={{ aspectRatio: "1 / 1" }}
                      />
                      <div className="absolute bottom-0 left-0 right-0 bg-gray-900 bg-opacity-60 text-white py-2 text-center">
                        <span className="text-sm font-semibold">
                          {category.name}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {selectedRestaurantId && selectedCategory && (
              <div>
                <h2 className="text-xl font-semibold mb-4">
                  Menu Items in {selectedCategory.name}
                </h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {menuItemsInCategory.map((item) => (
                    <div
                      key={item.id}
                      className="bg-white rounded-lg shadow-md overflow-hidden"
                    >
                      <img
                        src={`/images/${item.image}`} // Replace with actual path
                        alt={item.name}
                        className="w-full h-32 object-cover"
                      />
                      <div className="p-4">
                        <h3 className="text-lg font-semibold">{item.name}</h3>
                        <p className="text-gray-600 text-sm mb-2">
                          {item.description}
                        </p>
                        <p className="text-indigo-500 font-bold">
                          ${item.price.toFixed(2)}
                        </p>
                        {/* You might add edit/delete buttons here */}
                      </div>
                    </div>
                  ))}
                  {/* Button to add a new menu item to this category */}
                  <div
                    className="bg-gray-100 rounded-lg shadow-md flex items-center justify-center cursor-pointer hover:bg-gray-200"
                    onClick={openAddMenuItemModal}
                  >
                    <div className="text-gray-500">
                      <svg
                        className="w-10 h-10 mx-auto"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M12 6v6m0 0v6m0-6h6m-6 0H6"
                        />
                      </svg>
                      <p className="text-center text-sm">Add New Item</p>
                    </div>
                  </div>
                </div>
                <button
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-4 focus:outline-none focus:shadow-outline"
                  onClick={() => setIsAddMenuItemModalOpen(true)} // Open modal to add item
                >
                  Add New Menu Item to {selectedCategory.name}
                </button>
              </div>
            )}

            {!selectedRestaurantId && (
              <p className="mt-4 text-gray-500">
                Please select a restaurant to manage its menu items.
              </p>
            )}

            {/* Add New Category Modal */}
            <Modal
              isOpen={isAddCategoryModalOpen}
              onRequestClose={closeAddCategoryModal}
              style={{
                overlay: {
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  zIndex: 1000,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  overflowY: "auto",
                },
                content: {
                  position: "relative",
                  background: "#fff",
                  borderRadius: "8px",
                  padding: "24px",
                  maxWidth: "400px",
                  width: "90%",
                  maxHeight: "80vh",
                  overflowY: "auto",
                },
              }}
              contentLabel="Add New Category"
            >
              {/* ... (Add New Category Modal content remains the same) ... */}
            </Modal>

            {/* Modal for Adding New Menu Item to a Category */}
            <Modal
              isOpen={isAddMenuItemModalOpen}
              onRequestClose={closeAddMenuItemModal}
              style={{
                overlay: {
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  zIndex: 1000,
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  overflowY: "auto",
                },
                content: {
                  position: "relative",
                  background: "#fff",
                  borderRadius: "8px",
                  padding: "24px",
                  maxWidth: "600px",
                  width: "90%",
                  maxHeight: "80vh",
                  overflowY: "auto",
                },
              }}
              contentLabel={`Add New Menu Item to ${selectedCategory?.name}`}
            >
              <h2 className="text-xl font-semibold mb-4">
                Add New Menu Item to {selectedCategory?.name}
              </h2>
              {/* Form to add new menu item */}
              <form className="space-y-4">
                <div>
                  <label
                    htmlFor="itemName"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Item Name:
                  </label>
                  <input
                    type="text"
                    id="itemName"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemDescription"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Description:
                  </label>
                  <textarea
                    id="itemDescription"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  />
                </div>
                <div>
                  <label
                    htmlFor="itemPrice"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Price:
                  </label>
                  <input
                    type="number"
                    id="itemPrice"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  />
                </div>
                {/* Add more fields as needed (image upload, etc.) */}
                <div className="flex justify-end">
                  <button
                    type="button"
                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline"
                    onClick={closeAddMenuItemModal}
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    // onClick={handleSaveNewMenuItem} // Implement this
                  >
                    Save Item
                  </button>
                </div>
              </form>
            </Modal>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageMenu;
