// src/pages/customer-resturents/RestaurantsPage.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import CustomerHeader from "../../components/CustomerHeader";
import Footer from "../../components/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";

const DUMMY = [
  {
    id: 1,
    name: "The Curry Pot",
    location: "Colombo",
    img: "/images/mongolian-resturent.jpeg",
  },
  {
    id: 2,
    name: "Noodle House",
    location: "Kandy",
    img: "/images/deagon-resturent.jpeg",
  },
  {
    id: 3,
    name: "Sushi World",
    location: "Galle",
    img: "/images/sushi.jpeg",
  },
  {
    id: 4,
    name: "Pizza Palace",
    location: "Negombo",
    img: "/images/dominospizza-resturent.jpeg",
  },
  // …add more or fetch from your API
];

export default function RestaurantsPage() {
  const [query, setQuery] = useState("");

  // simple filter by name or location
  const filtered = DUMMY.filter(
    (r) =>
      r.name.toLowerCase().includes(query.toLowerCase()) ||
      r.location.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <div className="flex flex-col min-h-screen">
      <CustomerHeader />

      <main className="flex-grow container mx-auto px-4 py-8">
        <h1 className="text-2xl font-semibold mb-4">Search Restaurants</h1>

        <div className="mb-8 relative max-w-md">
          <input
            type="text"
            placeholder="Search by name or location..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-orange-400"
          />
          <FontAwesomeIcon
            icon={faSearch}
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
          />
        </div>

        {filtered.length ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filtered.map((r) => (
              <Link
                key={r.id}
                to={`/restaurants/${r.id}`}
                className="block bg-white rounded-lg shadow hover:shadow-md transition overflow-hidden"
              >
                <img
                  src={r.img}
                  alt={r.name}
                  className="w-full h-40 object-cover"
                />
                <div className="p-4">
                  <h2 className="font-semibold mb-1">{r.name}</h2>
                  <p className="text-sm text-gray-600">{r.location}</p>
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <p className="text-gray-500">No restaurants match “{query}”.</p>
        )}
      </main>

      <Footer />
    </div>
  );
}
