import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/NavBar";
import Header from "../components/Header";

function AdminRestaurants() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [restaurants, setRestaurants] = useState([]); // State to hold admin's restaurants

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/");
      return;
    }

    // In a real application, you would fetch the admin's restaurants here
    // using an API call. For now, we'll use some sample data.
    const sampleRestaurants = [
      {
        id: 1,
        name: "Delicious Diner",
        location: "Colombo",
        status: "Open",
        openTime: "9:00 AM",
        closeTime: "10:00 PM",
      },
      {
        id: 2,
        name: "Spicy Kitchen",
        location: "Kandy",
        status: "Closed",
        openTime: "11:00 AM",
        closeTime: "9:00 PM",
      },
      {
        id: 3,
        name: "Fresh Fusion",
        location: "Galle",
        status: "Open",
        openTime: "10:00 AM",
        closeTime: "11:00 PM",
      },
    ];
    setRestaurants(sampleRestaurants);

    // In a real application, you might fetch data like this:
    // api.get('/admin/restaurants').then(response => {
    //   setRestaurants(response.data);
    // }).catch(error => {
    //   console.error("Error fetching restaurants:", error);
    // });
  }, [user, navigate]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleAddNewRestaurant = () => {
    // Navigate to a page for adding a new restaurant
    navigate("/admin/restaurants/add");
  };

  const handleUpdateRestaurant = (restaurantId) => {
    // Navigate to a page for updating the restaurant details
    navigate(`/admin/restaurants/update/${restaurantId}`);
  };

  const handleDeleteRestaurant = (restaurantId) => {
    // Implement the logic to delete the restaurant
    console.log(`Deleting restaurant with ID: ${restaurantId}`);
    // In a real application, you would make an API call to delete.
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar (Navbar) */}
      <Navbar
        className={`${
          isSidebarOpen ? "w-64" : "w-0"
        } transition-all duration-300 ease-in-out`}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <Header onToggleSidebar={toggleSidebar} />

        {/* Page Content */}
        <main className="flex-1 bg-gray-100 p-4 overflow-y-auto">
          <div className="container mx-auto">
            <h2 className="text-2xl font-semibold mb-4">My Restaurants</h2>

            <button
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mb-4"
              onClick={handleAddNewRestaurant}
            >
              Add New Restaurant
            </button>

            {restaurants.length > 0 ? (
              <div className="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Name
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Location
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Status
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Open Time
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Close Time
                      </th>
                      <th scope="col" className="relative px-6 py-3">
                        <span className="sr-only">Edit/Delete</span>
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {restaurants.map((restaurant) => (
                      <tr key={restaurant.id}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {restaurant.name}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.location}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.status}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.openTime}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {restaurant.closeTime}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <button
                            onClick={() =>
                              handleUpdateRestaurant(restaurant.id)
                            }
                            className="text-indigo-600 hover:text-indigo-900 mr-2"
                          >
                            Update
                          </button>
                          <button
                            onClick={() =>
                              handleDeleteRestaurant(restaurant.id)
                            }
                            className="text-red-600 hover:text-red-900"
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p>No restaurants added yet.</p>
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

export default AdminRestaurants;
