import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../services/api"; // Assuming you've set up an API service for making requests

function ManageRestaurants() {
  const [restaurants, setRestaurants] = useState([]);

  // Fetch all restaurants from the backend
  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await api.get("/restaurant"); // Endpoint to fetch restaurants
        setRestaurants(response.data);
      } catch (error) {
        console.error("Error fetching restaurants:", error);
      }
    };

    fetchRestaurants();
  }, []);

  return (
    <div className="manage-restaurants-container">
      <div className="header">
        <h2>Manage Restaurants</h2>
        <Link to="/admin/restaurants/add">
          <button className="add-restaurant-btn">+ Add New Restaurant</button>
        </Link>
      </div>

      <div className="restaurant-tiles">
        {restaurants.length === 0 ? (
          <p>No restaurants found</p>
        ) : (
          restaurants.map((restaurant) => (
            <div className="restaurant-tile" key={restaurant._id}>
              <img src={restaurant.image} alt={restaurant.name} />
              <div className="restaurant-info">
                <h3>{restaurant.name}</h3>
                <p>{restaurant.description}</p>
                <p>{restaurant.address}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ManageRestaurants;
