import React, { useState } from "react";
import api from "../services/api";
import { useNavigate } from "react-router-dom"; // Import useNavigate

function Register() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("delivery");
  const [address, setAddress] = useState(""); // For customers
  const [location, setLocation] = useState(""); // For delivery drivers

  const navigate = useNavigate(); // Use useNavigate hook for navigation

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post("/auth/register", {
        firstName,
        lastName,
        email,
        username,
        password,
        role,
        address: role === "customer" ? address : undefined, // Include address for customers
        location: role === "delivery" ? location : undefined, // Include location for delivery
      });
      localStorage.setItem("x-auth-token", response.data.token); // Store token
      alert("User registered successfully!");

      // Redirect based on the user role
      if (response.data.user.role === "customer") {
        navigate("/customer-dashboard"); // Redirect to customer dashboard
      } else if (response.data.user.role === "delivery") {
        navigate("/driver-dashboard"); // Redirect to driver dashboard
      } else {
        navigate("/admin-dashboard"); // Redirect to admin dashboard
      }
    } catch (error) {
      console.error("Error registering:", error);
      alert("Error registering user.");
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="delivery">Delivery</option>
          <option value="admin">Admin</option>
          <option value="customer">Customer</option>
        </select>

        {role === "customer" && (
          <input
            type="text"
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required={role === "customer"}
          />
        )}

        {role === "delivery" && (
          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required={role === "delivery"}
          />
        )}

        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
